import React, { useState, useEffect } from 'react';
import './App.css';

function Login({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async () => {
    try {
      const response = await fetch('/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      });

      if (response.ok) {
        const data = await response.json();
        if (data && data.token) {
          onLogin(data.token);
        } else {
          setErrorMessage('Invalid response from server. Please try again later.');
        }
      } else {
        const errorMessage = await response.text(); // Get the error message from the response body
        setErrorMessage(errorMessage || 'Invalid username or password. Please try again.');
      }
    } catch (error) {
      console.error('Login error:', error);
      setErrorMessage('An error occurred. Please try again later.');
    }
  };


  return (
    <div className="login-container">
      <h2>Login</h2>
      <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
}

function Card({ fruit, flipped, chooseCard }) {
  const cardClickHandle = () => {
    chooseCard(fruit);
  };

  return (
    <div className={`card ${flipped ? 'matched' : ''}`} onClick={cardClickHandle}>
      <img style={{ transform: "rotateY(180deg)" }} src={fruit.src} alt={fruit.name} />
      <svg xmlns="http://www.w3.org/2000/svg" width="40" height="40" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" fill="none" strokeLinecap="round" strokeLinejoin="round">
        <path stroke="none" d="M0 0h24v24H0z" fill="none"></path>
        <path d="M8 8a3.5 3 0 0 1 3.5 -3h1a3.5 3 0 0 1 3.5 3a3 3 0 0 1 -2 3a3 4 0 0 0 -2 4"></path>
        <line x1="12" y1="19" x2="12" y2="19.01"></line>
      </svg>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [token, setToken] = useState('');
  const [fruits, setFruits] = useState([]);
  const [turns, setTurns] = useState(0);
  const [fruitOne, setFruitOne] = useState(null);
  const [fruitTwo, setFruitTwo] = useState(null);

  useEffect(() => {
    const fetchGame = async () => {
      try {
        const response = await fetch('/api/game', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (response.ok) {
          const data = await response.json();
          setFruits(data);
        } else {
          console.error('Failed to fetch game data');
        }
      } catch (error) {
        console.error('Fetch game error:', error);
      }
    };

    if (isLoggedIn) {
      fetchGame();
    }
  }, [isLoggedIn, token]);

  const handleLogin = (token) => {
    setIsLoggedIn(true);
    setToken(token);
  };

  const chooseCard = (fruit) => {
    fruitOne ? setFruitTwo(fruit) : setFruitOne(fruit);
    setTurns((prevTurns) => prevTurns + 1);
  };

  const initGame = () => {
    const allFruits = [...fruits, ...fruits]
      .sort(() => Math.random() - .5)
      .map((item) => ({ ...item, id: Math.random() }))
    setFruits(allFruits);
  };

  const resetGame = () => {
    setFruits(prevFruits => {
      return prevFruits.map(item => {
        if (item.matched) {
          return { ...item, matched: false }
        }

        return item
      })
    })

    setFruitOne(null)
    setFruitTwo(null)
    setTurns(0);

    setTimeout(() => {
      initGame()
    }, 500)
  }

  useEffect(() => {
    if (fruitOne && fruitTwo) {
      if (fruitOne.src === fruitTwo.src) {
        setFruits(prevFruits => {
          return prevFruits.map(item => {
            if (item.src === fruitOne.src) {
              return { ...item, matched: true }
            } else {
              return item
            }
          })
        })
      }

      setTimeout(() => {
        setFruitOne(null)
        setFruitTwo(null)
      }, 500)
    }
  }, [fruitTwo, fruitOne, fruits])

  return (
    <>
      {isLoggedIn ? (
        <div className="game-container">
          <h1>Memory Card Game</h1>
          <h3>Turns: {turns}</h3>
          <button className='reset' onClick={resetGame}>
            Reset Game
          </button>
          <div className="game-block">
            {fruits.map((fruit, key) => (
              <Card
                key={key}
                chooseCard={chooseCard}
                flipped={fruit === fruitOne || fruit === fruitTwo || fruit.matched}
                fruit={fruit}
              />
            ))}
          </div>
        </div>
      ) : (
        <div>
          <Login onLogin={handleLogin} />
        </div>
      )}
    </>
  );
}

export default App;
